import React, {useState, useEffect} from 'react';
import db from '../firebase';


function CoffeBreak(){
  const [data, setData] = useState([]);
  useEffect(()=>{
    db.collection('menu').get()
    .then((snapshot) => {
      const newData = snapshot.docs.map((doc) => ({ 
        ...doc.data()
      })) 
      setData(newData)
    })
  },[])
  return data
}

  const Cafe =() => {
  const menu = CoffeBreak();

  return (
    <>{ 

      menu.map((item)=>
      <button> {item.Nome} 
      {console.log(item)}
      </button>
    // <MyButton text={items.data().Nome}/>
      )}
    </>
  )
  }

export default Cafe;
