import React, {useState} from 'react';
import './App.css';

import db from './firebase';


db.collection('menu').get()
  .then((snapshot) => {
    snapshot.forEach((doc) => {
      console.log(doc.id, '=>', doc.data().breakfast);
    });
  })
  .catch((err) => {
    console.log('Error getting documents', err);
  });

function App() {
  constb[counter, setCounter] = useState(0);

  return (
    <div className="App">
     <h1>Tentativa firebase</h1>
     <div>
      <button onClick={() => setCounter(counter + 1)}>Contador</button>
     </div>
    </div>
  );
}

export default App;
