import React, {useState, useEffect} from 'react';
import db from '../firebase';
import MyButton from '../components/button'


function Menu(){
  const [data, setData] = useState([]);
  useEffect(()=>{
    db.collection('menu').get()
    .then((snapshot) => {
      const newData = snapshot.docs.map((doc) => ({ 
         ...doc.data()
      })) 
      setData(newData)
    })
  },[])
  return data
}






export default Cafe;
